import React from 'react'


function Todo(props) {
    // aca podemos recibir las props quqe las podemos usar para imprimir el titulo y la descripcion de cadsa tarea
    // function Todo({ todo }) {
    //haremos destructuracion desde el argumrnto del componente
    return (
        <div className='card mt-2'>
            <div className='card-body '>
                <h3 className='card-title text-right'>
                    {/* Titulo */}
                    {props.todo.title}
                    {/* aca podemos imprimir una sentencia javascrip y le decimos que del objeto props traeme el todo y del todo muestrame el title */}
                    {/* de donde sale este props.todo? viene del componente padre */}
                    {/* podemos simplificar mas esto */}
                    {/* {todo.title} */}
                    <button className='btn btn-sm btn-outline-success ml-2'>
                        Terminar
                    </button>
                </h3>
                <p className='card-text text-right'>
                    {/* Descripcion */}
                    {props.todo.description}
                    {/* podemos simplificar mas esto */}
                    {/* {todo.description} */}
                </p>
                <hr />
                <div className='d-flex justify-content-end'>
                    <button className='btn btn-sm btn-outline-primary'>
                        Editar
                    </button>
                    <button className='btn btn-sm btn-outline-danger'>
                        Eliminar
                    </button>
                </div>
            </div>
        </div>
    )
}

export default Todo
