import React from 'react'
import Todo from './Todo'



//props
// creamos estas props(objetos de tareas) por queremos que cada todo muestre informacion diferente 
// const todo1 = {
//     title: 'todo #1',
//     description: 'Descripcion Todo #1',
//     complete: false,
// }
// const todo2 = {
//     title: 'todo #2',
//     description: 'Descripcion Todo #2',
//     complete: true,
// }

// aca haremos qu cada todo forme parte de una lista quqe sera un arreglo dond el primer elemnto sera el todo1
const todos = [

    {
        title: 'todo #1',
        description: 'Descripcion Todo #1',
        complete: false,
    },
    {
        title: 'todo #2',
        description: 'Descripcion Todo #2',
        complete: true,
    }

]
//  ahora como renderizamos esta lista de todods en nuestro componente? primero quedaremos con 1 todo


function TodoList() {
    return (
        <div>
            <h1>TodoList</h1>


            {/* <Todo
                // aca tenemos una propiedad todo y recibe el contenido del objeto todo1
                //    enviamos todo de este componente padre
                todo={todo1} />
            <Todo
                // aca tenemos una propiedad todo y recibe el contenido del objeto todo2, tambien tenemos que modificar el componete
                todo={todo2} /> */}

            {/* despues de sacar los  Todo  ecribiremos una sentencia de js, para recorrer para recorrer nuestro objeto */}


            {/* // en el componente Todo enviamos cada uno de los todo que son sobre los que estamos iterando */}
            {
                todos.map(todo => <Todo todo={todo} />)
                // explicacion de esto: iteramos con map sobre nuestro arreglo y recorre cada elemento a los que les colocara el sobrenombre todo,
                // es decir en la primera iteracion este todo se refiere al primer elemento y en la segunda iteracion se refiere al segundo elemento,
                // y por cada iteracion retorna un componente Todo y este recibira una propiedad todo y a este todo le entregaremos la tarea sobre la 
                // que estaremos iterando(todo naranjo), en resumen le estamos entregando a la prop
                // el todo verde es el que estamos recibiendo en el componente tarea( en el Todo.js)
            }
        </div>
    )
}

export default TodoList
