import React, { useState, useEffect } from 'react'
//usaremos este nuevo hooks useEfect para no afectar el comportamiento actual de nuestra aplicacion
// que nos permitira que cuando el estado es este caso el todoedit cambie por un valor
// al presionar editar en cualquiera de las tarea y react al darse cuenta d eese cambio de eestado se 
// rellene la informacion de los form values con ese nuevo valor que estamos recibiendo, lo usaremos debajo de nuestros estados...
const initialFormValues = {
    title: '',
    description: '',

}

function TodoForm({ todoAdd, todoEdit, todoUpdate }) { //aca recibimos todoUpdate como una props y lo usamos en el handleSubmit
    const [formValues, setFormValues] = useState(initialFormValues)
    const { title, description } = formValues;
    const [error, setError] = useState(null)
    const [successMessage, setSuccessMessage] = useState(null)

    // recibe 2 argumento donde el segundo es un arreglo de dependencias(de useEfect) ...lo que significa que si coloco todoEdit cuando este cambie se ejecutara una funcion en el  primer argumento
    useEffect(() => {
        if (todoEdit) {
            setFormValues(todoEdit)
        } //aca estamos diciendo que si todoedit esta en null esto nunca se va a ejecutar


    }, [todoEdit]) //este arreglo de dependencias (el arreglo) puede tener muchas dependencias por ejemplo
    // [todoEdit,state2, state3,state4] y que uno de ellos cambie de valor se ejecuta el calback o funcion
    // en este caso solo necesitamos  un state que es el todoedit sera la funcion que el useEfect estara escuchando
    // tambien podriamos tener un arreglo vacio que indicaria ... si bien el useEfect siempre se ejecutara una vez al renderizarse el componente tenga 
    // o no tenga dependencias  y en caso de que no tenga dependencias solo se ejecutaraesa primera vez y ne caso de que tenga una dependencia se ejecutara la primera vez y adicionalmente
    // cada vez que esas dependencias por lo menos una cambie
    // entonces lo que queremos hacer y es cuando cambie el todoEdit  los fromValues  tomen lo que tiene el todoEdit

    const handleInputChange = (e) => {
        const changedFormValues = {
            ...formValues,
            [e.target.name]: e.target.value
        }
        setFormValues(changedFormValues)

    }
    const handleSubmit = (e) => {
        e.preventDefault()

        if (title.trim() === '') {
            setError('Debes Indicar un Titulo')
            return;

        }
        // hacemos lo mismo para description
        if (description.trim() === '') {
            setError('Debes Indicar una Descripcion')
            return;
        }


        // agregra tareas 
        // en vez de agregar una tarea haremos un condicional donde pregntaremos si el estado global todoEdit es diferente de null si esto ocurre quiere decir que estamos actualizando
        // y vamos a utilizar la funcion todoUpdate y vamos a enviar los formValues que en este caso tienen id y complete
        // y si no es asi agregaremos la tarea
        if (todoEdit) {
            // actualizando
            todoUpdate(formValues)
            setSuccessMessage('Actualizado con exito')

        } else {
            todoAdd(formValues)
            setSuccessMessage('Agregado con exito')
            setFormValues(initialFormValues)
        }


        // setFormValues(initialFormValues)// esto limpia nuestro formulario o lo vuelve a un etado inicial, esto puede ser conveniente para agregar entonces este comportamiento solo lo usaremos 
        // para agregar y no para actualizar

        setTimeout(() => {
            setSuccessMessage(null)
        }, 2000)

        setError(null)

    }
    return (
        <div>
            <h1>{todoEdit ? 'Editar Tarea' : 'Nueva Tarea'}</h1>
            <form onSubmit={handleSubmit}>
                {/* onSubmit sera igual a una funcion que no hemos agregado que llamaremos handlesubmit  y la crearemos mas arriba*/}
                <input
                    type='text'
                    placeholder='Titulo'
                    className='form-control'
                    value={title}
                    name='title'
                    onChange={handleInputChange}
                />
                <textarea
                    placeholder='Descripcion'
                    className='form-control mt-2'
                    value={description}
                    name='description' //conozcamos la utilidad de colocar este atributo (name) a cada input crearemos una funcion handleInputChange
                    onChange={handleInputChange}
                >
                </textarea>
                <button
                    className='btn btn-primary btn-block mt-2'>
                    {todoEdit ? 'Actulizar Tarea' : ' Agregar Tarea'}
                </button>
            </form>
            {
                error &&
                (
                    <div className='alert alert-danger mt-2'>{error}</div>
                )
            }
            {
                successMessage &&
                (
                    <div className='alert alert-success mt-2'>{successMessage}</div>
                )
            }
        </div>
    )
}
export default TodoForm
