// haciendo nuestra aplicacion persistente esto se refiere a que cuando se recargue la pagina el estado anterior se siga manteniendo por lo menos con llos todos
// asi haremos persistentes el estado de los todos , para ello usaremos el localstorage y lo hacemos aca en el app.js

import React, { useEffect, useState } from 'react'
import TodoForm from './components/TodoForm'
import TodoList from './components/TodoList'


const initialTodos = [
    {
        id: 1,
        title: 'todo #1',
        description: 'Descripcion Todo #1',
        completed: false,
    },
    {
        id: 2,
        title: 'todo #2',
        description: 'Descripcion Todo #2',
        completed: true,
    }
]

// aca creamos localTodos , pero por que afuera del componente?
// es por que no tiene que ver con logica de estado , es para que no nos confunda al leer el codigo

// esto es un strin y necesitamos convertirlo de nuevo a un array de objetos usando JSON.parse
const localTodos = JSON.parse(localStorage.getItem('todos'))


function App() {
    const [todos, setTodos] = useState(initialTodos)
    const [todoEdit, setTodoEdit] = useState(null)

    // crearemos un nuevo effect que debemos importarlo antes
    // esto recibe el arreglo de dependencias y la funcion que se va a ejecutar cuando una de esas dependencias cambie
    // pero que dependencias va a escuchar este efecto, que es lo que queremos hacer , que cada vez que los todos sean diferentes se grabe en localstorage un valor actualizado
    // de los todos, por lo tanto nuestras dependencias deben ser los todos
    // revisemos la seccion de aplicacion en las herramientas del desarrollador donde hay una seccion aplicacion donde que dice almacenamiento ahi vamos al localhost3000 que es en la direccion que estamos trabajando en este momento
    // aca comenzaremos grabando los todos cada vez que se actualice el estado de los todos
    useEffect(() => {


        // esta funcion va a recibir como primer parametro el nombre de la clave que queremos asignar en el localstorage y como 
        //  segundo parametro el nombre de lo que queremos almacenar es decir la variable todo que en este momento contiene un estado
        // automaticamnete nos guardo un valos debido a que el useefectt se ejecuta una vez minimo siempre y tambien cuando se camboa alguna de las dependencias
        // por ahora vemos que el valor es object object y se debe que localsotorage almacena solo string
        // pero hay una forma de convertir este objeto (todos,el parametro 2) a string (usando JSON.stringify) y luego hacer el proceso contrario para volver a utilizarlo
        // este metodo JSON.stringify convertiria cualquier objeto a string
        localStorage.setItem('todos', JSON.stringify(todos))
        // pero  al recargar no se guardaron nuestros cambio debido a que no los estamos cargando al iniciar nuestra aplicacion
        // esto lo haremos fuera del componente creando una variable

    }, [todos])



    const todoDelete = (todoId) => {

        if (todoEdit && todoId === todoEdit.id) {
            setTodoEdit(null)
        }
        const changedTodos = todos.filter(todo => todo.id !== todoId)
        setTodos(changedTodos);
    }

    const todoToogleCompleted = (todoId) => {
        const changedTodos = todos.map(todo => todo.id === todoId ? { ...todo, completed: !todo.completed } : todo)

        setTodos(changedTodos)
    }

    const todoAdd = (todo) => {

        const newTodo = {
            id: Date.now(),
            ...todo,
            completed: false
        }
        const changedTodos = [
            newTodo,
            ...todos,

        ]

        setTodos(changedTodos)
    }



    // creando nuestra funcion para actualizar
    const todoUpdate = (todoEdit) => {
        const changedTodos = todos.map(todo => (
            todo.id === todoEdit.id
                ? todoEdit
                : todo
        ))

        setTodos(changedTodos)
    }

    return (
        <div className='container mt-4'>
            <div className='row'>
                <div className='col-8'>
                    <TodoList
                        todos={todos}
                        todoDelete={todoDelete}
                        todoToogleCompleted={todoToogleCompleted}
                        setTodoEdit={setTodoEdit}
                    />
                </div>
                <div className='col-4'>
                    <TodoForm
                        // estados
                        todoEdit={todoEdit}
                        // funciones
                        todoAdd={todoAdd}
                        todoUpdate={todoUpdate}
                        setTodoEdit={setTodoEdit}
                    />


                </div>

            </div>
        </div>

    )
}

export default App
