//aca trabajeremos en editar una tarea para ello creaemos un estado global pero por que un estado global?
// por que nos servira para hacer una comunicacion entre componentes es por ello que necesitamos que este en un punto alto de la aplicacion
// o mas alto de los componentes que lo van a utilizar(totoList y todoForm)
// al hacer clic en editar se cargara la tarea en el formulario


import React, { useState } from 'react'
import TodoForm from './components/TodoForm'
import TodoList from './components/TodoList'

// esto lo trajimos desde TodoList
const initialTodos = [
    {
        id: 1,
        title: 'todo #1',
        description: 'Descripcion Todo #1',
        completed: false,
    },
    {
        id: 2,
        title: 'todo #2',
        description: 'Descripcion Todo #2',
        completed: true,
    }
]

function App() {
    const [todos, setTodos] = useState(initialTodos)

    const [todoEdit, setTodoEdit] = useState(null) //este state tiene la tarea que se esta actualizando actualmente(tanto title como description) es decir que cuando no sea null va a ser un objeto
    // necesitamos actualizar este todoEdit usando el boton de editar para ello a cada todo vamos a enviarle setTodoEdit para que se pueda actualizar desde ese componente 
    // pero para llegar al todo primero debemos pasar por todoList y le pasaremos setTodoEdit para que s epueda actualizar ese componente
    const todoDelete = (todoId) => {
        const changedTodos = todos.filter(todo => todo.id !== todoId)
        setTodos(changedTodos);
    }

    const todoToogleCompleted = (todoId) => {
        const changedTodos = todos.map(todo => todo.id === todoId ? { ...todo, completed: !todo.completed } : todo)

        setTodos(changedTodos)
    }

    const todoAdd = (todo) => {

        const newTodo = {
            id: Date.now(),
            ...todo,
            completed: false
        }
        const changedTodos = [
            newTodo, //para que la nueva tarea se agregue en la parte superior
            ...todos,
            //usamos operador spred
            // newTodo // lo cambiamos arriba
        ]

        setTodos(changedTodos)
    }
    // esta funcion la usaremos en /odoForm

    return (
        <div className='container mt-4'>
            <div className='row'>
                <div className='col-8'>
                    <TodoList
                        todos={todos}
                        todoDelete={todoDelete}
                        todoToogleCompleted={todoToogleCompleted}
                        setTodoEdit={setTodoEdit}// y lo recibimos en el todoList
                    />
                </div>
                <div className='col-4'>
                    <TodoForm
                        todoAdd={todoAdd} />
                    {/* esta propiedad la recibimos en los props de todoform.js */}
                </div>

            </div>
        </div>

    )
}

export default App
