import React from 'react'

function Todo({ todo, todoDelete, todoToogleCompleted }) { //recibimos la propiedad todoDelete y como la usaremos? al hacer 
    // click en eliminar se dispare un evento que llame la funcion, vamos al boton eliminar
    // recibimos la propiedad todoToogleCompleted para usarla en el boton terminar

    return (
        <div className='card mt-2'>
            <div className='card-body '>
                <h3 className='card-title text-right'>
                    {todo.title}
                    <button
                        // en el evento onClick debe ejecutar la funcion todoToogleCompleted la que recibe como argumento el id del todo que qqueremos marcaar como  compltado
                        // recordar que sin funcion de flecha la funcion se ejecutaria auatomaticamente por recibir un argumento y eso no lo queremos
                        onClick={() => todoToogleCompleted(todo.id)} // como mostramos este cambio de manera visual??
                        // className='btn btn-sm btn-outline-success ml-2'> // comentamos esto por que usaremos como un templatestring
                        className={`btn btn-sm btn-outline-success ml-`}>

                        {/* haremos una sentencia que diga que este boton mostrara un texto distinto cuando este Terminado o este por Terminar dejaremos comentado el texto que aparece en el boton (Treminar)*/}
                        {/* Terminar  */}

                        {todo.completed ? 'Terminado' : 'terminar'}
                        {/* ahora cargaremos una clase condicional ver clasName  que lo comvetiremos en un templateString*/}
                    </button>
                </h3>
                <p className='card-text text-right'>
                    {todo.description}
                </p>
                <hr />
                <div className='d-flex justify-content-end'>
                    <button className='btn btn-sm btn-outline-primary'>
                        Editar
                    </button>
                    <button
                        onClick={() => todoDelete(todo.id)}
                        className='btn btn-sm btn-outline-danger'>

                        Eliminar
                    </button>
                </div>
            </div>
        </div >
    )
}

export default Todo
