// que pas si enviamos una tarea vacia? hasta ahora igual se agrega  lo qu no debria ser 
// lo quqe solucionaremos creando un avalidacion que compruebe que tanto el titulo y la dessripcion
// deeben tener contenido para guardarse, lo haremos en submit


import React, { useState } from 'react'

const initialFormValues = {
    title: '',
    description: '',

}

function TodoForm({ todoAdd }) {
    const [formValues, setformValues] = useState(initialFormValues)
    const { title, description } = formValues;
    const [error, setError] = useState(null)


    const handleInputChange = (e) => {
        const changedFormValues = {
            ...formValues,
            [e.target.name]: e.target.value
        }
        setformValues(changedFormValues)

    }
    const handleSubmit = (e) => {
        e.preventDefault() //  previniendo el efecto por defaut del formulario

        if (title === '') {
            //title eya lo estamos destructurando de los formValues para utilizarlos de una forma mas facil
            setError('Debes Indicar un Titulo') //para mostrar esto en pantalla por que solo lo vemos developertools veamos abajo al despues del formulario
            // donde agregamos un div
            return;
            // tambien mostraremos por pantalla al usuario por que no se garega la tarea
        }
        // hacemos lo mismo para description
        if (title === '') {
            setError('Debes Indicar una Descripcion')
            return;
        }
        // y si esta agrega la nueva tarea el error lo devuel al estado inicial .... null quqe no hay error

        todoAdd(formValues)
        setError(null)
    }
    return (
        <div>
            <h1>Nueva Tarea</h1>
            <form onSubmit={handleSubmit}>
                {/* onSubmit sera igual a una funcion que no hemos agregado que llamaremos handlesubmit  y la crearemos mas arriba*/}
                <input
                    type='text'
                    placeholder='Titulo'
                    className='form-control'
                    value={title}
                    name='title'
                    onChange={handleInputChange}
                />
                <textarea
                    placeholder='Descripcion'
                    className='form-control mt-2'
                    value={description}
                    name='description' //conozcamos la utilidad de colocar este atributo (name) a cada input crearemos una funcion handleInputChange
                    onChange={handleInputChange}
                >
                </textarea>
                <button
                    className='btn btn-primary btn-block mt-2'>
                    Agregar Tarea
                </button>
            </form>
            {/* {
                error
                    ? (
                        <div className='alert alert-danger mt-2'>{error}</div>
                    ) : (
                        null
                    )
            }   lo de mas abajo esta  mas resumido*/}
            {
                error &&
                (
                    <div className='alert alert-danger mt-2'>{error}</div>
                )
            }
        </div>
    )
}
export default TodoForm
