// crearemos este componente
//que es una funcion, puede ser de clase o funcionales

import React, { useState } from 'react'
import TodoForm from './components/TodoForm'
import TodoList from './components/TodoList'

const initialTodos = [
    {
        id: 1,
        title: 'todo #1',
        description: 'Descripcion Todo #1',
        complete: false,
    },
    {
        id: 2,
        title: 'todo #2',
        description: 'Descripcion Todo #2',
        complete: true,
    }
]

function App() {
    const [todos, setTodos] = useState(initialTodos)

    // aca crearemos una funcion que nos permita eliminar, quqe sera funcion flecha que recibe el id del todo que vamos a eliminar
    const todoDelete = (todoId) => {
        // pero como funciona esa actualizacion de estado en React, recordar que nuestro estado son todos los todos(el arreglo )
        // por lo que necesitamos enviar un nuevo estado

        //  entonces despues de la linea de mas abajo diremos que estos changedTodos seran una cte y vas a ser igual a los todos y para poder
        // eliminaaar una tarea especifica basaandose en el id que estamos recibiendo usaremos filter que por cada iteracion (que en la primera iteracion sera el primer objeto
        // y en la segunda iteracion sera el segundo objeto) y diremos que por cada todo me devuelva la condicion : que todo.id (que es id que estamos iterando en 1 y el 2)
        // que sea diferente al id que recibimos en el argumento(de cte todoDelete)
        // ahora necesitamos enviar esta funcion al componente donde la usaremos, como no se puede pasar directamenta a cada todo especifico( que son lo que estamos renderizando  )
        // lo hacemos por el componente padre (en este caso TodoList) ver mas abajo
        const changedTodos = todos.filter(todo => todo.id !== todoId)

        // esta funcion quqe se llama setTodos va a recibir un nuevo estado co o changedTodos
        setTodos(changedTodos);
    }

    return (
        <div className='container mt-4'>
            <div className='row'>
                <div className='col-8'>
                    <TodoList
                        todos={todos}
                        todoDelete={todoDelete} />  {/* la recibimos como todoDelete y la enviamos como {todoDelete} ahora vamos al TodoList y recimos la funcion como parametro */}

                </div>
                <div className='col-4'>
                    <TodoForm />
                </div>

            </div>
        </div>

    )
}

export default App
