import React, { useState } from 'react'
import Todo from './Todo'

// haremos una carga condicional cuando no haya ninguna tarea en el listado lo haremos debajo del titulo

function TodoList({ todos, todoDelete, todoToogleCompleted, setTodoEdit }) { //  recibimos la funcion todoDelete que viene desde App de aqui se la enviamos a cada todo y se la pasamos al componente Todo ver mas abajo

    return (
        <div>
            <h1>TodoList</h1>

            {/* preguntamos si las tareas los todos es un arreglo vacio si es asi muestra el texto o en caso contrario siga mostrando lalista de tareas */}

            {todos.lengh === 0
                ? (<div className='alert alert-primary'>
                    No hay Tareas , agrega una!!!
                </div>)
                :
                (todos.map(todo => (
                    <Todo
                        todo={todo}
                        key={todo.id}
                        todoDelete={todoDelete} //ahora ya la funcion la podemos usar en componente Todo
                        todoToogleCompleted={todoToogleCompleted} //la enviamos al Todo
                        setTodoEdit={setTodoEdit} //  recibimos la funcion todoDelete que viene desde App de aqui se la enviamos a cada todo y se la pasamos al componente Todo ver mas abajo

                    />
                )))
            }


            {

            }
        </div >
    )
}

export default TodoList
