import React, { useState } from 'react'

const initialFormValues = {
    title: '',
    description: '',

}

function TodoForm({ todoAdd }) {
    const [formValues, setformValues] = useState(initialFormValues)
    const { title, description } = formValues;
    const [error, setError] = useState(null)
    const [successMessage, setSuccessMessage] = useState(null)

    const handleInputChange = (e) => {
        const changedFormValues = {
            ...formValues,
            [e.target.name]: e.target.value
        }
        setformValues(changedFormValues)

    }
    const handleSubmit = (e) => {
        e.preventDefault()

        if (title.trim() === '') {
            setError('Debes Indicar un Titulo')
            return;

        }
        // hacemos lo mismo para description
        if (description.trim() === '') {
            setError('Debes Indicar una Descripcion')
            return;
        }

        // agregar tareas
        todoAdd(formValues)
        setformValues(initialFormValues)// limpiando el formulario luego de agregar una tarea, enviamo los datos inicialaes
        setSuccessMessage('Agregado con exito')

        setTimeout(() => {
            setSuccessMessage(null)
        }, 2000)

        setError(null)

    }
    return (
        <div>
            <h1>Nueva Tarea</h1>
            <form onSubmit={handleSubmit}>
                {/* onSubmit sera igual a una funcion que no hemos agregado que llamaremos handlesubmit  y la crearemos mas arriba*/}
                <input
                    type='text'
                    placeholder='Titulo'
                    className='form-control'
                    value={title}
                    name='title'
                    onChange={handleInputChange}
                />
                <textarea
                    placeholder='Descripcion'
                    className='form-control mt-2'
                    value={description}
                    name='description' //conozcamos la utilidad de colocar este atributo (name) a cada input crearemos una funcion handleInputChange
                    onChange={handleInputChange}
                >
                </textarea>
                <button
                    className='btn btn-primary btn-block mt-2'>
                    Agregar Tarea
                </button>
            </form>
            {
                error &&
                (
                    <div className='alert alert-danger mt-2'>{error}</div>
                )
            }
            {
                successMessage &&
                (
                    <div className='alert alert-success mt-2'>{successMessage}</div>
                )
            }
        </div>
    )
}
export default TodoForm
