import React, { useState } from 'react'
import TodoForm from './components/TodoForm'
import TodoList from './components/TodoList'

// esto lo trajimos desde TodoList
const initialTodos = [
    {
        id: 1,
        title: 'todo #1',
        description: 'Descripcion Todo #1',
        completed: false,
    },
    {
        id: 2,
        title: 'todo #2',
        description: 'Descripcion Todo #2',
        completed: true,
    }
]

function App() {
    const [todos, setTodos] = useState(initialTodos)
    const todoDelete = (todoId) => {
        const changedTodos = todos.filter(todo => todo.id !== todoId)
        setTodos(changedTodos);
    }
    const todoToogleCompleted = (todoId) => {
        // const changedTodos = todos.map(todo => (
        //     todo.id === todoId
        //         ? { ...todo, completed: !todo.completed }//esta linea seria todoEdit que cambiamos por esta
        //         : todo
        // ))
        // dejando esto solo en una linea
        const changedTodos = todos.map(todo => todo.id === todoId ? { ...todo, completed: !todo.completed } : todo)

        setTodos(changedTodos)
    }

    // aca agregamos la nueva tarea , agragamos una accion para agregar un nuevo todo
    // esta vez no recibe un todo id sino mas bien recibe un objeto todo que sera enviado desde 
    // el formulario que traera el estado formvalue tendra el title y description y necesitaremos actualizar los todos con setTodos

    const todoAdd = (todo) => {

        const newTodo = {
            id: Date.now(),
            ...todo,
            completed: false
        }
        const changedTodos = [
            ...todos,
            //usamos operador spred
            newTodo
        ]

        setTodos(changedTodos)
    }
    // esta funcion la usaremos en /odoForm

    return (
        <div className='container mt-4'>
            <div className='row'>
                <div className='col-8'>
                    <TodoList
                        todos={todos}
                        todoDelete={todoDelete}
                        todoToogleCompleted={todoToogleCompleted}
                    />
                </div>
                <div className='col-4'>
                    <TodoForm
                        todoAdd={todoAdd} />
                    {/* esta propiedad la recibimos en los props de todoform.js */}
                </div>

            </div>
        </div>

    )
}

export default App
